let percent=document.querySelector(".loading-progress"),bg=document.querySelector(".background"),load=0,int=setInterval(blurring,50);const scale=(e,l,r,t,n)=>(e-l)*(n-t)/(r-l)+t;function blurring(){load++,load>99&&clearInterval(int),percent.innerText=load+"%",percent.style.opacity=scale(load,0,100,1,0),bg.style.filter=`blur(${scale(load,0,100,60,0)}px)`}