let blocks=document.querySelectorAll(".block");function checkBlocks(){let e=window.innerHeight/5*4;blocks.forEach((c,o)=>{c.getBoundingClientRect().top<e?(c.style.backgroundImage="url('https://source.unsplash.com/random/800x600?sig="+o,c.classList.add("show")):(c.style.backgroundImage="none",c.classList.remove("show"))})}window.addEventListener("scroll",checkBlocks),checkBlocks();